import React from 'react';

class Products extends React.Component {
    render() {
        return (
            <>
          <div>
          <nav class="navigation">
            <div class="menuToggle">
              <input type="checkbox" />
              <span></span>
              <span></span>
              <span></span>
              <ul class="menu">
                <a href="#">
                  <li>Home</li>
                </a>
                <a href="#">
                  <li>Products</li>
                </a>
                <a href="#">
                  <li>Contact</li>
                </a>
              </ul>
            </div>
          </nav>
          </div>

          <h1 class="heading">Products</h1>

            </>
        )
    }
}

class List extends React.Component {
    state = {}

    componentDidMount () {
        fetch('./Products.json')
        .then(res => res.json())
        .then(this.onLoad);
    }
    parseData(response) {
        return response.data;
    }
    onLoad = (data) => {
        this.setState({
            data: this.parseData(data)
        });
    }
    render() {
        const { data } = this.state;
        return data ? 
            this.renderData(data) :
            this.renderLoading()
    }
    renderData(data) {
        if (data && DataTransfer.length){
            return (
                <div>
                    {
                        data.map(item => (
                            <div key={item.id}>
                                <a href={`mailto:${item.email}`}></a>{item.company}
                            </div>
                        ))
                    }
                </div>
            );
        }else{
            return <div>No Items Found</div>
        }
    }
    renderLoading() {
        return <div>Loading...</div>
    }
}
