import React from 'react';
import Data from '../Data/products.json';
import ProductCard from '../ProductCard';

class Filters  extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productData: Data, 
            priceRange: "default",
            productType: "default"
        };
    }
    handlePriceRangeDropDown = (event) => {
        this.setState({
            priceRange: event.target.value,
        })
    }
    handleProductTypeDropDown = (event) => {
        this.setState({
            productType: event.target.value,
        })
    }
    render() {
        const { priceRange, productType, productData } = this.state;
        return (
            <>
                 <section className="myBtnContainer">
                        <select id="productType" value={productType} onChange={this.handleProductTypeDropDown}>
                            <option value="default">Show All</option>
                            <option value="Clothes">Clothing</option>
                            <option value="Accessories">Accessories</option>
                        </select>
                        <select id="productPrice" value={priceRange} onChange={this.handlePriceRangeDropDown}>
                            <option value="default">Show All</option>
                            <option value="$0-$10">$0 - $10</option>
                            <option value="$11-$20">$11 - $20</option>
                            <option value="$21-$30">$21 - $30</option>
                    </select>
                </section>
                 <div className="productRow">
                    {productData.map((item) => <ProductCard item={item}/> )} 
                </div> 

            </>
        );
    }

};

export default Filters;
