    constructor(props) {
        super(props);
        this.state = {
            price: "default",
            type: "default"
        };
    }
    handlePriceDropDown = (event) => {
        this.setState({
            price: event.target.value,
            type: event.target.value
        })
    }
    render() {
        console.log(Data);
