import React from 'react';
import Data from '../Data/products.json';
import ProductData from '../ProductData/ProductData';

class Filters  extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            price: "default",
            type: "default"
        };
    }
    handlePriceDropDown = (event) => {
        this.setState({
            price: event.target.value,
            type: event.target.value
        })
    }
    render() {
        const { price, type } = this.state;
        return (
            <>
                 <section className="myBtnContainer">
                        <select id="productType" value={type} onChange={this.handlePriceDropDown}>
                            <option value="default">Show All</option>
                            <option value="Clothes">Clothing</option>
                            <option value="Accessories">Accessories</option>
                        </select>
                        <select id="productPrice" value={price} onChange={this.handlePriceDropDown}>
                            <option value="default">Show All</option>
                            <option value="$0-$10">$0 - $10</option>
                            <option value="$11-$20">$11 - $20</option>
                            <option value="$21-$30">$21 - $30</option>
                    </select>
                </section>
                 <div className="productRow">
                {Data.map((item) => {
                    if (type === 'default' &&  price === 'default') {
                        return <ProductData item={item}/>
                      } else if (type === item.type && price === item.price) {
                        return <ProductData item={item} />
                      } else if (type === item.type || price === item.price) {
                        return <ProductData item={item} />
                      }
                //     if (price === "default" && type === "default" ) {
                //          return <ProductData item={item}  />
                //       } else if (price === item.price || type === item.type ) {
                //          return <ProductData item={item}  />
                //  } else if (price === item.price && type === item.type) {
                //      return <ProductData item={item}  />
                //  }
                        })} 
                </div> 

            </>
        );
    }

};

export default Filters;
